mapper.update:
UPDATE students
SET
  name = #{name}, name_kana = #{nameKana}, nickname = #{nickname}, email = #{email},
  area = #{area}, birth_date = #{birthDate}, gender = #{gender}, remark = #{remark}, deleted = #{deleted}
WHERE id = #{id}

UPDATE students_courses
SET
  course_name = #{courseName}, expected_end_date = #{expectedEndDate}
WHERE
  id = #{id} AND student_id = #{studentId}
